mapper.select:
SELECT * FROM ( SELECT rownum rn, a.* FROM ( SELECT * FROM Client
DEL_STATUS = 0
AND client_Name LIKE '%' || #{client_Name} || '%'
AND client_Gubun = #{client_Gubun}
AND client_Man LIKE '%' || #{client_Man} || '%'
ORDER BY client_no ) a ) WHERE rn BETWEEN #{start} AND #{end}

mapper.update:
		UPDATE client 
		SET emp_no = #{empDTO.empNo}, client_name = #{client_Name}, client_gubun = #{client_Gubun}
          , client_email = #{client_Email}, client_man = #{client_Man}
          , client_address = #{client_Address}, modify_date = SYSDATE
        WHERE client_no = #{client_No}

UPDATE client
SET del_status = 1
WHERE client_no = #{client_No}
