mapper.select:
SELECT count(*) 
FROM (
  SELECT a.*, ROWNUM rn 
  FROM (
    -- 실제 데이터 쿼리
    SELECT *
    FROM parts

      del_status = 0

        AND parts_status = #{parts_status}


        AND parts_name LIKE '%' || #{parts_name} || '%'


        AND TRUNC(in_date) >= #{startDate}


        AND TRUNC(in_date) <= #{endDate}


    ORDER BY parts_no
  ) a
)

SELECT * 
FROM (
  SELECT a.*, ROWNUM rn 
